import React, { useState } from 'react';
import './Style1.css'; // custom styling

export default function Header() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const closeSidebar = () => {
    setSidebarOpen(false);
  };

  return (
    <div>
      <div className="container">
        <div className="row" style={{ height: 'auto', backgroundColor: 'aliceblue', padding: '15px' }} id="navbar">
          <div className="col-6 col-lg-3 d-flex align-items-center">
            <strong>Navbar</strong>
          </div>

          {/* Hamburger icon for small screens */}
          <div className="col-6 d-lg-none d-flex justify-content-end align-items-center">
            <button onClick={toggleSidebar} className="btn btn-outline-primary">
              â˜°
            </button>
          </div>

          {/* Desktop menu */}
          <div className="d-none d-lg-block col-lg-6">
            <ul style={{ listStyle: 'none', display: 'flex', justifyContent: 'flex-end', margin: 0, padding: 0 }}>
              <li>Home</li>
              <li>About</li>
              <li>Services</li>
              <li>Products</li>
              <li>Contact</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Sidebar for small screen */}
      <div className={`custom-sidebar ${sidebarOpen ? 'open' : ''}`}>
        <button className="btn btn-danger mb-3" onClick={closeSidebar}>X</button>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li className="mb-2">Home</li>
          <li className="mb-2">About</li>
          <li className="mb-2">Services</li>
          <li className="mb-2">Products</li>
          <li className="mb-2">Contact</li>
        </ul>
      </div>
    </div>
  );
}
